import React from "react";
import "./Home.scss";
import cover from "../../src/moviecover.jpg";
const Card=({img})=>(
    <img className='card' src={img} alt="cover"/>
    
)

const Row=({title,arr=[{img:cover}]})=>{
    return (
        <div className="row">
          <h2>{title}</h2>
          <div>
            {
                arr.map((item)=>(
                    <Card img={"item.img"}/>
                ))
            }

          
          

          </div>

        </div>
      );
    };
const Home = () => {
  return (
    <section className="home">
      <div className="banner"></div>

      <Row title={"Popular on Netflix"} />
    </section>
  );
};

export default Home;
